    /*
     * Adding a new camara
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
                'dirip' => $this->input->post('dirip'),
            );
            
            $camara_id = $this->Camara_model->add_camara($params);
            redirect('camara/index');
        }
        else
        {            
            $data['_view'] = 'camara/add';
            $this->load->view('layouts/main',$data);
        }
    }  
