    /*
     * Editing a camara
     */
    function edit($id)
    {   
        // check if the camara exists before trying to edit it
        $data['camara'] = $this->Camara_model->get_camara($id);
        
        if(isset($data['camara']['id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
                    'dirip' => $this->input->post('dirip'),
                );

                $this->Camara_model->update_camara($id,$params);            
                redirect('camara/index');
            }
            else
            {
                $data['_view'] = 'camara/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The camara you are trying to edit does not exist.');
    } 
