    /*
     * Deleting camara
     */
    function remove($id)
    {
        $camara = $this->Camara_model->get_camara($id);

        // check if the camara exists before trying to delete it
        if(isset($camara['id']))
        {
            $this->Camara_model->delete_camara($id);
            redirect('camara/index');
        }
        else
